import React, { useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

interface InputFormProps {
  onCalculate: (inputValues: InputValues) => void;
}

export interface InputValues {
  cartValue: number | null;
  deliveryDistance: number | null;
  numberOfItems: number | null;
  orderTime: string | null;
}

const InputForm: React.FC<InputFormProps> = ({ onCalculate }) => {
  const [inputValues, setInputValues] = useState<InputValues>({
    cartValue: null,
    deliveryDistance: null,
    numberOfItems: null,
    orderTime: null,
  });

  const handleInputChange = (field: keyof InputValues, value: string) => {
    setInputValues({
      ...inputValues,
      [field]: field === 'orderTime' ? value : parseFloat(value),
    });
  };

  const handleCalculate = () => {
    onCalculate(inputValues);
  };

  return (
    <form aria-labelledby="inputFormTitle">
      <h2 id="inputFormTitle">Delivery Information</h2>

      <div>
        <label htmlFor="cartValue">Cart value:</label>
        <input
          type="number"
          id="cartValue"
          data-test-id="cartValue"
          onChange={(e) => handleInputChange('cartValue', e.target.value)}
          aria-describedby="cartValueError"
        />
      </div>

      <div>
        <label htmlFor="deliveryDistance">Delivery distance:</label>
        <input
          type="number"
          id="deliveryDistance"
          data-test-id="deliveryDistance"
          onChange={(e) => handleInputChange('deliveryDistance', e.target.value)}
          aria-describedby="deliveryDistanceError"
        />
      </div>

      <div>
        <label htmlFor="numberOfItems">Number of items:</label>
        <input
          type="number"
          id="numberOfItems"
          data-test-id="numberOfItems"
          onChange={(e) => handleInputChange('numberOfItems', e.target.value)}
          aria-describedby="numberOfItemsError"
        />
      </div>

      <div>
        <label htmlFor="orderTime">Order time:</label>
        <input
          type="datetime-local"
          id="orderTime"
          data-test-id="orderTime"
          onChange={(e) => handleInputChange('orderTime', e.target.value)}
          aria-describedby="orderTimeError"
        />
      </div>

      <button onClick={handleCalculate}>Calculate Delivery Fee</button>

      {/* Error messages */}
      <div id="cartValueError" style={{ color: 'red' }}>
        {/* Include error message based on your validation logic */}
        {inputValues.cartValue === null || isNaN(inputValues.cartValue) ? 'Cart value is required.' : ''}
      </div>

      <div id="deliveryDistanceError" style={{ color: 'red' }}>
        {/* Include error message based on your validation logic */}
        {inputValues.deliveryDistance === null || isNaN(inputValues.deliveryDistance)
          ? 'Delivery distance is required.'
          : ''}
      </div>

      <div id="numberOfItemsError" style={{ color: 'red' }}>
        {/* Include error message based on your validation logic */}
        {inputValues.numberOfItems === null || isNaN(inputValues.numberOfItems) ? 'Number of items is required.' : ''}
      </div>

      <div id="orderTimeError" style={{ color: 'red' }}>
        {/* Include error message based on your validation logic */}
        {inputValues.orderTime === null ? 'Order time is required.' : ''}
      </div>
    </form>
  );
};

export default InputForm;
