// src/App.tsx

import React, { useState } from 'react';
import InputForm, { InputValues } from './Components/InputField';
import DeliveryFeeCalculator from './Components/DeliveryFeeCalculator';

function App() {

  const [inputValues, setInputValues] = useState<InputValues>({
    cartValue: null,
    deliveryDistance: null,
    numberOfItems: null,
    orderTime: null,
  });

  const calculateDeliveryFee = (inputValues: InputValues) => {
    // Set the input values in the state
    setInputValues(inputValues);

  };

  const calculateDeliveryFeeLogic = (inputValues: InputValues): number | null => {
    const { cartValue, deliveryDistance, numberOfItems, orderTime } = inputValues;

    if (cartValue === null || deliveryDistance === null || numberOfItems === null || orderTime === null) {
      // Input values are not complete, cannot calculate the fee
      return null;
    }

    // Replace this with your actual logic based on the provided rules
    const smallOrderSurcharge = Math.max(0, 10 - cartValue);
    let baseFee = 2;
    const additionalDistanceFee = Math.ceil((deliveryDistance - 1000) / 500) * 1;
    const itemSurcharge = numberOfItems >= 5 ? (numberOfItems - 4) * 0.5 : 0;
    const bulkFee = numberOfItems > 12 ? 1.2 : 0;
    let totalFee = baseFee + additionalDistanceFee + smallOrderSurcharge + itemSurcharge + bulkFee;
    totalFee = Math.min(totalFee, 15);

    const orderDate = new Date(orderTime);
    const isFridayRush = orderDate.getUTCDay() === 5 && orderDate.getUTCHours() >= 15 && orderDate.getUTCHours() < 19;

    if (isFridayRush) {
      totalFee *= 1.2;
      totalFee = Math.min(totalFee, 15);
    }

    return totalFee;
  };

  return (
    <div className="App">
      <InputForm onCalculate={calculateDeliveryFee} />
      <DeliveryFeeCalculator inputValues={inputValues} />
    </div>
  );
}

export default App;
